function initBrandSlick() {
	if ($(window).width() > 560)
		return (
			$(".brand-section .offer-cards").hasClass("slick-initialized") &&
				$(".brand-section .offer-cards").slick("unslick"),
			!0
		);
	$(".brand-section .offer-cards").not(".slick-initialized").slick({
		dots: !0,
		arrows: !1,
		infinite: !0,
		centerMode: !0,
		autoplay: !1,
		slidesToShow: 1,
		slidesToScroll: 1,
		centerPadding: "15px",
	});
}
function timerInit() {
	var countDownDate = new Date("22 Aug 2020 18:22:25").getTime(),
		x = setInterval(function () {
			var now = new Date().getTime(),
				distance = countDownDate - now,
				days = Math.floor(distance / 864e5),
				hours = Math.floor((distance % 864e5) / 36e5),
				minutes = Math.floor((distance % 36e5) / 6e4),
				seconds = Math.floor((distance % 6e4) / 1e3),
				timeRemains = "";
			days > 0 && (timeRemains += days + "d "),
				1 == hours.toString().length && (hours = 0 + hours.toString()),
				1 == minutes.toString().length && (minutes = 0 + minutes.toString()),
				1 == seconds.toString().length && (seconds = 0 + seconds.toString()),
				(timeRemains += hours + "." + minutes + "." + seconds),
				$("span.remaining-time").text(timeRemains),
				distance < 0 &&
					(clearInterval(x), $("span.remaining-time").text("EXPIRED"));
		}, 1e3);
}
$(document).ready(function () {
	initBrandSlick(),
		timerInit(),
		$(".az-icon-close").hide(),
		$(".az-icon ").click(function () {
			$(this).hide(), $(".brand-nav").show(), $(".az-icon-close").show();
		}),
		$(".az-icon-close").click(function () {
			$(this).hide(), $(".brand-nav").hide(), $(".az-icon").show();
		}),
		$(window).bind("scroll", function () {
			$(".brand-lists .brand-list").each(function (index) {
				if (
					!($(".card-loading").length > 0) &&
					$(this).isOnScreen() &&
					$(this).hasClass("hidden-card")
				) {
					var thisElement = $(this);
					thisElement.removeClass("hidden-card"),
						thisElement.addClass("card-loading"),
						setTimeout(function () {
							thisElement.removeClass("card-loading"),
								thisElement.addClass("auto-height");
						}, 2e3);
				}
			});
		});
}),
	$(window).resize(function () {
		initBrandSlick();
	});

